/**
 * Return an array of the return values generated by calling the given function for each element in the given array
 * @param {*[]} arr - array with elements to be consumed by the given function
 * @param {function} fun - function that will be called for each element in arr
 */
const runOnEach = (arr, fun) => (!arr || !fun ? [] : arr.map(fun));

module.exports = {
  runOnEach,
};
